function.body:
/************************************************************************* * The contents of this file are subject to the Openbravo Public License * Version 1.1 (the "License"), being the Mozilla Public License * Version 1.1 with a permitted attribution clause; you may not use this * file except in compliance with the License. You may obtain a copy of * the License at http://www.openbravo.com/legal/license.html * Software distributed under the License is distributed on an "AS IS" * basis, WITHOUT WARRANTY OF ANY KIND, either express or implied. See the * License for the specific language governing rights and limitations * under the License. * The Original Code is Openbravo ERP. * The Initial Developer of the Original Code is Openbravo SLU * All portions are Copyright (C) 2012-2019 Openbravo SLU * All Rights Reserved. * Contributor(s): ______________________________________. ************************************************************************/ v_percent NUMBER:=0; v_isinvoiced VARCHAR2(1):='N'; v_issotrx CHAR(1); BEGIN SELECT COALESCE(issotrx, 'N') INTO v_issotrx FROM m_inout WHERE m_inout_id = p_minoutid;
IF v_issotrx = 'Y' THEN SELECT CASE WHEN SUM(ABS(COALESCE(del.movementqty, 0))) = 0 THEN 0 ELSE ROUND(SUM(ABS(COALESCE(inv_si.qtymatched, 0))) / SUM(ABS(COALESCE(del.movementqty, 0))) * 100, 0) END INTO v_percent FROM m_inoutline del LEFT JOIN ( SELECT msi.m_inoutline_id, SUM(msi.qty) AS qtymatched FROM m_matchsi msi JOIN c_invoiceline il ON il.c_invoiceline_id = msi.c_invoiceline_id JOIN c_invoice i ON i.c_invoice_id = il.c_invoice_id WHERE i.processed = 'Y' AND i.docstatus NOT IN ('VO', 'CL', 'DR') GROUP BY msi.m_inoutline_id ) inv_si ON inv_si.m_inoutline_id = del.m_inoutline_id WHERE del.m_inout_id = p_minoutid; ELSE SELECT CASE WHEN SUM(ABS(COALESCE(del.movementqty, 0))) = 0 THEN 0 ELSE ROUND(SUM(ABS(COALESCE(inv_pi.qtymatched, 0))) / SUM(ABS(COALESCE(del.movementqty, 0))) * 100, 0) END into v_percent FROM m_inoutline del LEFT JOIN ( SELECT mi.m_inoutline_id, SUM(mi.qty) AS qtymatched FROM m_matchinv mi JOIN c_invoiceline il ON il.c_invoiceline_id = mi.c_invoiceline_id JOIN c_invoice i ON i.c_invoice_id = il.c_invoice_id WHERE i.processed = 'Y' AND i.docstatus NOT IN ('VO', 'CL', 'DR') GROUP BY mi.m_inoutline_id ) inv_pi ON inv_pi.m_inoutline_id = del.m_inoutline_id WHERE del.m_inout_id = p_minoutid; END IF;
IF (v_percent IS NULL) THEN SELECT iscompletelyinvoiced INTO v_isinvoiced FROM m_inout WHERE m_inout_id =p_minoutid; IF (v_isinvoiced = 'Y') THEN v_percent:=100; ELSE v_percent:=0; END IF; ELSIF (v_percent > 100) THEN v_percent:=100; END IF;
RETURN v_percent; END C_GETINVOICESTATUSFROMSHIPMENT
