  v_Processed VARCHAR(60) ;
  v_C_INVOICE_ID VARCHAR2(32) ;

  v_only_taxamt   BOOLEAN;
  v_within_margin BOOLEAN;
BEGIN

    IF AD_isTriggerEnabled()='N' THEN RETURN;
    END IF;

  IF INSERTING THEN
    v_C_INVOICE_ID:=:new.C_INVOICE_ID;
  ELSE
    v_C_INVOICE_ID:=:old.C_INVOICE_ID;
  END IF;
  SELECT PROCESSED
  INTO v_Processed
  FROM C_INVOICE
  WHERE C_INVOICE_ID=v_C_INVOICE_ID;
  IF UPDATING THEN
    v_only_taxamt := COALESCE(:OLD.taxbaseamt,0) = COALESCE(:NEW.taxbaseamt,0)
    AND COALESCE(:OLD.c_tax_id,'0') = COALESCE(:NEW.c_tax_id,'0')
    AND COALESCE(:OLD.ad_org_id,'0') = COALESCE(:NEW.ad_org_id,'0')
    AND COALESCE(:OLD.ad_client_id,'0') = COALESCE(:NEW.ad_client_id,'0')
    AND COALESCE(:OLD.taxamt,0) <> COALESCE(:NEW.taxamt,0);v_within_margin :=
    ABS(COALESCE(:NEW.taxamt,0) - COALESCE(:NEW.origtaxamt, :NEW.taxamt, 0)) <= 0.01;
    IF v_Processed = 'Y' AND NOT (v_only_taxamt AND v_within_margin) THEN
      RAISE_APPLICATION_ERROR(-20000, '@20501@'  || ' - ' || :OLD.taxbaseamt || ' - ' || :NEW.taxbaseamt || ' - ' || :OLD.taxamt || ' - ' || :NEW.taxamt || ' - ' || :OLD.c_tax_id || ' - ' || :NEW.c_tax_id);
    END IF;
  END IF;
  IF((DELETING OR INSERTING) AND v_Processed='Y') THEN
    RAISE_APPLICATION_ERROR(-20000, '@20501@') ;
  END IF;
END C_INVTAX_CHK_RESTRICTIONS_TRG
