V_COUNT NUMBER; BEGIN IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
SELECT COUNT(1) INTO V_COUNT FROM FACT_ACCT WHERE AD_TABLE_ID = (SELECT AD_TABLE_ID FROM AD_TABLE WHERE UPPER(TABLENAME) LIKE 'M_MATCHSI') AND RECORD_ID = :OLD.M_MATCHSI_ID; IF (V_COUNT>0) THEN RAISE_APPLICATION_ERROR(-20000, '@MatchInvPosted@'); END IF; END M_MATCHSI_TRG
